#!/usr/bin/bash

getlogo() {
  local name="${1}"
  local opts="?labelColor=24292f&logo="
  local tag='data:image/svg+xml;base64,'
  local stopwatch='data:image/svg+xml;base64,'
  local calendar='data:image/svg+xml;base64,'
  local code='data:image/svg+xml;base64,'
  local shield='data:image/svg+xml;base64,'

  case $name in
    "tag")
      echo -n "${opts}${tag}"
      ;;
    "calendar")
      echo -n "${opts}${calendar}"
      ;;
    "stopwatch")
      echo -n "${opts}${stopwatch}"
      ;;
    "code")
      echo -n "${opts}${code}"
      ;;
    "shield")
      echo -n "${opts}${shield}"
      ;;
    "")
      echo -n ""
      ;;
    *)
      echo -n "${name}"
      ;;
  esac
}

getlogo "$1"
